import React, { Component } from "react";
import Button from "./components/Button";

class App extends Component {

  constructor(props) {
    super(props);

    this.state ={
      current: '0',
      previous: [],
      isZero: true,
      isDecimal: false
    };

  }

  reset = () =>{
    this.setState({
      current: '0',
      previous: [],
      isZero:true,
      isDecimal: false
    });
  }

  addToCurrent = (newNumber) =>{
    if(this.state.isZero && !this.state.isDecimal && newNumber !== "."){
      this.setState({
        current: String(newNumber),
        isZero: false
      });
    }else if (this.state.isDecimal){

    }else {
      this.setState({
        current: String(this.state.current + newNumber)
      });
    }
  }
  addSymToCurrent = (newNumber) =>{
    this.setState({
      previous: String(this.state.current + newNumber),
      current:'0'
    });
  }

  render(){
    const buttons = [
      {symbol:'C', cols: 3, action: this.reset},
      {symbol:'/', cols: 1, action: this.addSymToCurrent},
      {symbol:'7', cols: 1, action: this.addToCurrent}, 
      {symbol:'8', cols: 1, action: this.addToCurrent}, 
      {symbol:'9', cols: 1, action: this.addToCurrent}, 
      {symbol:'*', cols: 1, action: this.addSymToCurrent}, 
      {symbol:'4', cols: 1, action: this.addToCurrent}, 
      {symbol:'5', cols: 1, action: this.addToCurrent}, 
      {symbol:'6', cols: 1, action: this.addToCurrent}, 
      {symbol:'-', cols: 1, action: this.addSymToCurrent}, 
      {symbol:'1', cols: 1, action: this.addToCurrent}, 
      {symbol:'2', cols: 1, action: this.addToCurrent}, 
      {symbol:'3', cols: 1, action: this.addToCurrent},
      {symbol:'+', cols: 1, action: this.addSymToCurrent},
      {symbol:'.', cols: 1, action: this.addToCurrent},
      {symbol:'0', cols: 2, action: this.addToCurrent}, 
      {symbol:'=', cols: 1, action: this.addToCurrent},       

    ];

    return (
      <div className="App">
        <input className="result" type="text" value={this.state.current}></input>
        {buttons.map((btn,i) => {
          return(
            <div>
              <Button key={i} symbol={btn.symbol} cols={btn.cols} action={(symbol) => btn.action(symbol)}></Button>
            </div>
          )
        })}
      </div>

    );
  }
}

export default App;
